  styleUrls: ['./employee-registration.component.css']
})
export class EmployeeRegistrationComponent implements OnInit {

  departments:any[]=[];

  //dependancy injection
constructor(private http:HttpClient){

}

  ngOnInit(): void {
    this.loadDepartmanet();

  }
